CREATE TABLE "public"."files" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "path" text NOT NULL, "extension" text NOT NULL, "name" text NOT NULL, "size" integer NOT NULL, "status" integer NOT NULL, "createdAt" timestamptz NOT NULL DEFAULT now(), "createdBy" text, "updatedAt" timestamptz NOT NULL DEFAULT now(), "updatedBy" text, "userId" uuid NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("userId") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("path"));
CREATE EXTENSION IF NOT EXISTS pgcrypto;
