CREATE TABLE "public"."shares" ("userId" uuid NOT NULL, "fileId" uuid NOT NULL, "status" integer NOT NULL, "createdAt" timestamptz NOT NULL DEFAULT now(), "createdBy" text, "updatedAt" timestamptz NOT NULL DEFAULT now(), "updatedBy" text, PRIMARY KEY ("userId","fileId") , FOREIGN KEY ("userId") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("fileId") REFERENCES "public"."files"("id") ON UPDATE restrict ON DELETE restrict);
